mapper.update:
update concert_info set subject = #{subject}, summary = #{summary}, content = to_date(#{concert_date}, 'YYYY-MM-DD HH24:MI'),
	concert_date = #{concert_date}, place_name = #{place_name}, place_x = #{place_x}, place_y = #{place_y},
	info_file_path = #{info_file_path, jdbcType=VARCHAR}
where concert_number = #{concert_number}
