mapper.select:
select *
from board_info b, user_info u
where b.user_id = u.user_id and board_number=#{board_number}

mapper.update:
update board_info set
	view_count = view_count+1
where board_number = #{board_number}

update board_info set
	subject = #{subject},
	user_id = #{user_id},
	content = #{content}
where board_number = #{board_number}

mapper.delete:
	update board_info set
    	is_del = 'Y'
	where board_number = #{board_number}

mapper.select:
select file_path
from board_file
where board_number = #{board_number}
